from enum import Enum

from cl_instr_lib.base_classes.tek_scopes.acquire import Acquire
from cl_instr_lib.base_classes.tek_scopes.oscope_tek_base import namedtuple_min_max


class Acquire(Acquire):
    """Class providing the **Acquisition** controls of a Tektronix MSO[456] series oscilloscope."""

    def __init__(self, scope, **kwargs):
        super().__init__(scope, **kwargs)

        # Redefine parameters used in the parent class.

        self.enum_acq_modes = Enum('enum_acq_modes', {
            'SAMPLE': 'SAMPLE',
            'AVERAGE': 'AVERAGE',
            'PEAKDETECT': 'PEAKDETECT',
            'HIRES': 'HIRES',
            'ENVELOPE': 'ENVELOPE'
        })  # WFMDB not implemented.

        self.bounds_ave_num_wfms = namedtuple_min_max(2, 10240)

    def btn_clear(self):
        """Clears acquisitions, measurements and waveforms. Corresponds to clear button on the front panel in upper
        right corner.
        """

        self.write('CLEAR')
