from cl_instr_lib.drivers.audio.apx.bench.measurements.fft import Fft
from cl_instr_lib.drivers.audio.apx.bench.measurements.sweep import Sweep
from cl_instr_lib.drivers.audio.apx.bench.measurements.acoustic_response import AcousticResponse
from cl_instr_lib.drivers.audio.apx.bench.measurements.continuous_sweep import ContinuousSweep
from cl_instr_lib.drivers.audio.apx.bench.measurements.recorder import Recorder


class Measurements:

    """Container class for all Measurements"""

    def __init__(self, apx):
        self._apx = apx
        self.fft = Fft(apx)
        self.sweep = Sweep(apx)
        self.acoustic_response = AcousticResponse(apx)
        self.continuous_sweep = ContinuousSweep(apx)
        self.recorder = Recorder(apx)


