from cl_instr_lib.drivers.audio.apx.bench.measurements.monitors.fft import MonitorFft
from cl_instr_lib.drivers.audio.apx.bench.measurements.monitors.scope import Scope


class Monitors:
    """Container Class for APX Monitors"""

    def __init__(self, apx):
        self._apx = apx
        self.fft = MonitorFft(apx)
        self.scope = Scope(apx)
