from cl_instr_lib.base_classes.instrument_visa import InstrumentVisa

class Generic(InstrumentVisa):
    """Driver class for a Generic instrument. Allows access to the methods provided by the Instrument class."""

    def __init__(self, visa_resource_name=None, logger=None, **kwargs):
        super().__init__(visa_resource_name, logger,
                         model_re='^.*$',
                         sim_idn='',
                         **kwargs)


if __name__ == '__main__':
    generic = Generic('USB0::0x0699::0x0503::C010989')
    generic.debug_options(log_scpi=True, inst_error_handling='LOG_ALWAYS')
