from cl_instr_lib.base_classes.DMM2000_base import DMM2000_base


class DMM2000(DMM2000_base):

    def __init__(self, visa_resource_name=None, logger=None, **kwargs):

        super().__init__(visa_resource_name, logger,
                         model_re='^MODEL 2000$',
                         sim_idn='KEITHLEY INSTRUMENTS INC.,MODEL 2000,0608798,A13  /A02',
                         sim_opt='0,200X-SCAN',
                         **kwargs)

        terminals = self.get_fr_switch()
        if terminals != 'FRONT':
            self.log.warning("REAR terminals selected. To use the front "
                             "panel terminals, the INPUTS button must be pushed manually!")


if __name__ == '__main__':
    dmm2000 = DMM2000('GPIB0::17')   # Replace visa_resource_name with None for simulated hardware.
    dmm2000.debug_options(log_scpi=True, inst_error_handling='LOG_ALWAYS')

