from cl_instr_lib.base_classes.tek_afg.afg_3k_base import Afg3kBase, ProgrammingBounds

class AFG3152C(Afg3kBase):
    """Driver class for Tektronix AFG3152C Dual Output function generator"""

    def __init__(self, visa_resource_name=None, logger=None, **kwargs):
        chan_pgm_bounds = ProgrammingBounds(
            load_z_min=1.0, load_z_max=10000.0,
            ampl_pp_hiz_min=0.04, ampl_pp_hiz_max=20.0,  # Open circuit load, scaled for other loads in function_level.
            phase_min=-180, phase_max=180,
            offset_hiz_min=-10, offset_hiz_max=10,  # Open circuit load, scaled for other loads in function_level.
            voltage_window_hiz_min=-10, voltage_window_hiz_max=10,
            # TODO: Make freq_min a dictionary
            freq_min=1e-3, freq_max={
                'SINE': 150e6, 'SQUARE': 100e6, 'PULSE': 100e6, 'ARB': 100e6, 'OTHER': 1.5e6
            },
            freq_sine_burst_min=1e-3, freq_sine_burst_max=50e6,
            freq_arb_burst_min=1e-6, freq_arb_burst_max=75e6,  # TODO: See if min works on physical device
            ramp_symmetry_min=0.0, ramp_symmetry_max=100.0,
            edge_min=3e-9,
            duty_min=0.1, duty_max=99.999,
            noise_percent_max=50.0,
            am_depth_percent_max=120.0,
            fm_deviation_max=75e6,                     # TODO: See data sheet is function of wave shape
            pm_deviation_max=180.0,
            mod_freq_internal_min=2e-3, mod_freq_internal_max=50e3,
            fsk_keyrate_min=2e-3, fsk_keyrate_max=1e6,
            hop_freq_hz_max=100e6,
            burst_ncycles_min=1, burst_ncycles_max=1_000_000,
            burst_tdelay_sec_max=85.0
        )

        # Setup the simulation code inherited from Instrument.
        super().__init__(visa_resource_name, chan_pgm_bounds, logger,
                         model_re='^AFG3152C$',
                         sim_idn='TEKTRONIX,AFG3152C,C010131,SCPI:99.0 FV:1.0.10',
                         **kwargs)

if __name__ == '__main__':
    afg3152c = AFG3152C('GPIB0::12')    # 'USB0::0x0699::0x034C::C010789')  # Replace visa_resource_name with None for simulated hardware.
    afg3152c.debug_options(log_scpi=True, unused_args_handling='LOG_UNUSED', inst_error_handling='LOG_ALWAYS')
