from cl_instr_lib.base_classes.rs_smb.smb_base import SmbBase, ProgrammingBounds

class SMB100A(SmbBase):
    """Driver class for R&S SMB100A Signal Generator"""

    def __init__(self, visa_resource_name=None, logger=None, **kwargs):

        chan_pgm_bounds = ProgrammingBounds()

        super().__init__(visa_resource_name, chan_pgm_bounds, logger, model_re='^SMB100A$',
                         sim_idn = 'Rohde&Schwarz,SMB100A,1406.6000k02/113646,3.1.19.15-3.20.390.24',
                         **kwargs)

if __name__ == '__main__':
    smb100a = SMB100A('USB0::0x0AAD::0x0054::113646')
    smb100a.debug_options(log_scpi=True, unused_args_handling='LOG_UNUSED', inst_error_handling='LOG_ALWAYS')


