from cl_test_station.components.fpga.emu_wrapper.emu_wrapper import EmuWrapper


class SimEmuWrapper(EmuWrapper):

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)

    def configure(self, config):
        super().configure(config)

    def get_c4_edition(self):
        c4_edition = self.c4
        return c4_edition

    def get_build_rev(self):
        """
        **PLACEHOLDER!**

        :return:
        """
        if self.build_rev is None:
            c4_edition = hex(self.get_c4_edition())[2:].zfill(4)
            self.build_count = hex(self.build & 0xFFFF)[2:].zfill(4)
            self.build_rev = "e%s_%s" % (c4_edition, self.build_count)
        return self.build_rev
