"""
Definitions for global attributes used by HCC modules

Author: Evan Canter <evan.canter@cirrus.com>
"""
from cl_test_station.interfaces.ctrl_interface import ByteOrder

# # ----- Globals ----- # #
HCC_BYTE_ORDER = ByteOrder.LSB_FIRST
HCC_PLD_CHK_SIZE = 4  # Size in Bytes of all payload checksums
HCC_RSP_CHK_SIZE = 2  # Size in Bytes of Device RSP checksum
HCC_HDR_CHK_SIZE = 2  # Size in Bytes of Host and Device HDR checksum
HCC_RSP_SIZE = 4      # Size in Bytes of Host HCC RSP phase
HCC_PROC_DLY = 4      # Length in Bytes of delay between HDR_CHK_* and Payload bytes
HCC_HDR_SIZE = 8      # Length of HDR Phase in Bytes
HCC_PAD_VALUE = 0     # 0 or 1; value for PROC_DLY and Payload pad bits
EP_WORD_WIDTH = 4     # Endpoint access must be a multiple of this variable
DMAX_WORD_WIDTH = 4   # DMAX access must be a multiple of this variable
DEFAULT_CS_DELAY = 1  # Default deassertion time (usec) of CS line for queue'd transfers


def reset_to_default():
    """
    Resets all constants in this module to their default value

    :return: None
    :rtype: None
    """
    global HCC_BYTE_ORDER, HCC_PLD_CHK_SIZE, HCC_RSP_CHK_SIZE, HCC_HDR_CHK_SIZE, HCC_RSP_SIZE, HCC_PROC_DLY, \
        HCC_HDR_SIZE, HCC_PAD_VALUE, EP_WORD_WIDTH, DMAX_WORD_WIDTH, DEFAULT_CS_DELAY
    HCC_BYTE_ORDER = ByteOrder.LSB_FIRST
    HCC_PLD_CHK_SIZE = 4
    HCC_RSP_CHK_SIZE = 2
    HCC_HDR_CHK_SIZE = 2
    HCC_RSP_SIZE = 4
    HCC_PROC_DLY = 4
    HCC_HDR_SIZE = 8
    HCC_PAD_VALUE = 0
    EP_WORD_WIDTH = 4
    DMAX_WORD_WIDTH = 4
    DEFAULT_CS_DELAY = 1


reset_to_default()
