from collections import namedtuple

__i2c_mode = namedtuple('I2cMode', [
    'MODE_00',
    'MODE_01',
    'MODE_10',
    'MODE_11',
    'STD_8',
    'STD_16',
    'STD_32'
])
i2c = __i2c_mode(*range(7))

__i2c_std_mode = namedtuple('I2cStdMode', [
    'NOT_STD',
    'STD_8_BITS',
    'STD_16_BITS',
    'STD_32_BITS'
])
i2c_std = __i2c_std_mode(*range(4))
