#!/bin/bash
# Builds netifaces from source into given python version
# args: python version of installation, ex './install_netifaces 3.7'
cd /usr/local/lib/python$1/dist-packages/embedded_thrift_server/utilities
tar xvzf netifaces-0.10.9.tar.gz
cd netifaces-0.10.9
python$1 setup.py install
cd ..
rm -r netifaces-0.10.9
echo 'done'