from cl_test_station.components.dut.emu_dut.emu_dut import EmuDut
from franco_framework.components.franco.franco import Franco


class FrancoEmu(Franco, EmuDut):
    """Franco emulated DUT class"""

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)

    def initialize(self, **kwargs):
        super().initialize(**kwargs)

    def shutdown(self, **kwargs):
        super().shutdown(**kwargs)

    def enter_reset(self, *args, **kwargs):
        super().enter_reset(*args, **kwargs)

    def exit_reset(self, *args, **kwargs):
        super().exit_reset(*args, **kwargs)
