
from cl_test_station.components.component.component import Component


class Tmp117(Component):

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        # self.required_fields.extend(['Bits'])


    def read_temp_degC(self):
        Temp_reading=self.GLOBAL.TEMP.value
        Temp_reading_msb = Temp_reading >> 15
        if Temp_reading_msb == 1:
            Temp_reading = -(65536 - Temp_reading)

        Temp_reading = Temp_reading*1e-3*7.8125

        return Temp_reading