from franco_framework.test_stations.franco_test_station import FrancoTestStation


class FrancoValTestStation(FrancoTestStation):
    """Franco Validation Test Station class"""

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)

    def prepare_host_controllers(self, **kwargs):
        pass

    def initialize(self, **kwargs):
        super().initialize(**kwargs)
        # adding instruments to TestStation
        for key, value in self.get_instruments().items():
            self.__setattr__(key, value)
        
    def shutdown(self, *args, **kwargs):
        super().shutdown(*args, **kwargs)
 
