"""
Base class for Remote salad tests
"""
from .um_baseclass import UmBaseTest
from unified_modules.test_support.testbaseclass import BaseTest


class BaseRemoteSalad(UmBaseTest, BaseTest):
    """
    Provides common functionality requires across fruit-salad tests
    """
    def setup(self):
        super(BaseRemoteSalad, self).setup()
        self.fs_target.set_config_and_target(self.test_system.target_device.split('_')[0].capitalize(),
                                             self.test_system.config_file,
                                             self.test_system.device_file)

    def run_setup(self):
        BaseTest.run_setup(self)

    def run_test(self):
        BaseTest.run_test(self)

    def run_teardown(self):
        BaseTest.run_teardown(self)
