"""
Base class for all WISCE based fruit-salad test scripts
"""
import os
import shutil
import time

from .um_baseclass import UmBaseTest
from unified_modules.test_support.testbaseclass import BaseTest


class BaseWisceSaladException(Exception):
    """
    Exception class for exceptions in Base WISCE salad
    """
    pass


class BaseWisceSalad(UmBaseTest, BaseTest):
    """
    Base class for all WISCE based fruit-salad test scripts
    """
    def setup(self):
        """
        Set fruit-salad test_config files and target device
        """
        super(BaseWisceSalad, self).setup()
        self.fs_target.set_config_and_target(self.test_system.target_device.upper(), self.test_system.config_file,
                                             self.test_system.device_file)

    def run_setup(self):
        BaseTest.run_setup(self)

    def run_test(self):
        BaseTest.run_test(self)

    def run_teardown(self):
        BaseTest.run_teardown(self)
