class AttributeDict(dict):
    """
    Class to access dictionary keys as attributes
    """
    def __getattr__(self, attr):
        """
        Gets the attribute
        @param attr: dict key to get
        @return: value of the dict key
        """
        return self[attr]

    def __setattr__(self, attr, value):
        """
        Set the attribute
        @param attr: dict key to set
        @param value: value of the dict key
        @return: None
        """
        self[attr] = value