#
# Requires
#
require_relative '../../src/ProjectUsage.rb'

###############################################################################
#
# @!method Given_Project_Step
#
# Exemplar project step that prints a message.
#
#       Usage:
#       Given a custom step definition I have defined
#
###############################################################################
Given( /^a custom step definition I have defined$/ ) do 
    puts 'Given_Project_Step'
end

###############################################################################
#
# @!method Given_Project_Step_Src
#
# Exemplar project step that prints a message from a user defined module.
#
#       Usage:
#       Given a custom step definition I have defined using src method
#
###############################################################################
Given( /^a custom step definition I have defined using src method$/ ) do 
    puts ProjectUsage::fetch_message
end

