    # [DeviceID]. In case firmware changes based on the Hardware Revision it can
    # be specified using the key [DeviceID, HardwareRevision]. For example, for
    # Clearwater RevA Device ID is 25400 and HW Revision is 0 and firmware
    # for this can be specified using key KEY_DEVICE_ID_CLEARWATER_REVA =
    # [25400, 0]. Note that DeviceID and Hardware Revision are mentioned in
    # Decimal format.
    #
    ###############################################################################
    @@firmware = {
