class Functions {
    public:
        typedef std::vector<string> Arguments;
        typedef int (*Function)(const Arguments&);
        typedef std::map<string, Function> Table;

    private:
        Table table;
        
    public:
        int CallFunction( const string &name, const Arguments &arguments );
        void RegisterFunction( const string &name, Function pointer );
        
        static Functions* GetGlobal();

};

