# ensure that the gem version of test-unit is loaded
# otherwise the report generation will not occur.
gem 'test-unit'
require 'test/unit'

require_relative '../src/fruitsalad_env_setup.rb'
require_relative '../src/pops_detection.rb'

exit unless ENV['TARGET'].nil?

class TestPopsDetect < Test::Unit::TestCase
    def testPopsClicksDetection
        exitCode = NativePopsDetection::pops_clicks_detection(
                                        'resources/glitch2.wav' )
        assert_equal( :POPS_DETECTED, exitCode,
                      'Pops has not been detected though there is pop' )

        exitCode = NativePopsDetection::pops_clicks_detection(
                                        'resources/Driving_Before_DSP.wav' )
        assert_equal( :NO_POPS, exitCode,
                      'Pops detected though there is no pop' )
    end
end
