from collections import namedtuple


__packet_type = namedtuple('PacketType', [
    'ITM',
    'SOURCE',
    'PROTOCOL',
    'SYNC',
    'INSTRUMENTATION',
    'HARDWARE',
    'OVERFLOW',
    'LOCAL_TS',
    'GLOBAL_TS',
    'EXTENSION',
    'EVENT_COUNTER',
    'EXCEPTION',
    'PERIODIC_PC',
    'PC_SAMPLE',
    'PC_SLEEP',
    'TRACE',
    'TRACE_ADDRESS',
    'TRACE_DATA',
])

packet_type = __packet_type(*list(range(18)))


__payload_size = namedtuple('PayloadSize', [
    'BYTES_1',
    'BYTES_2',
    'BYTES_4',
])
payload_size = __payload_size(1, 2, 3)

__trace_address_type = namedtuple('DataTraceType', [
    'PC',
    'OFFSET',
])
trace_address_type = __trace_address_type(0, 1)

__transfer_direction = namedtuple('TransferDirection', [
    'READ',
    'WRITE',
])
transfer_direction = __transfer_direction(0, 1)

__timestamp_format = namedtuple('TimestampFormat', [
    'LONG',
    'SHORT',
])
timestamp_format = __timestamp_format(1, 2)

__global_timestamp_format = namedtuple('GlobalTimestampFormat', [
    'GTS1',
    'GTS2',
])
global_timestamp_format = __global_timestamp_format(1, 2)

data_size = namedtuple('data_size', 'BYTE HALFWORD WORD')(8, 16, 32)

trace_parsing_state = namedtuple('parse_state', 'WAIT_FOR_DATA WAIT_FOR_ADDRESS WAIT_FOR_TS')(0, 1, 2)
