class SquishCommon():

    @classmethod
    def GetChildrenOfType(cls,parent_real_name, type):
        children = []
        parentobj=waitForObject(parent_real_name)        
        for childobj in object.children(parentobj):
            if childobj.type == type:
                children.append(childobj)
        #test.log("Child ref list "+str(children)+"With label" + type)
        return children




