"""
Unified Test Runner entry point

"""

import sys
from unified_modules.test_runner import test_runner


def main():
    """
    Main entry point for the runner.
    """

    parser = test_runner.BaseTestRunner.get_argument_parser()
    options, extra = parser.parse_known_args()
    if options.non_tms:
        runner = test_runner.NonTmsTestRunner()
    else:
        runner = test_runner.QacTestRunner()

    options, extra = parser.parse_known_args()
    return_code = runner.run(options, extra)

    sys.exit(return_code)


if __name__ == '__main__':
    main()
